(419, '2019-08-22', '2019-09-05', 'REFORMA', 'LEY ORGÁNICA DEL CONGRESO DE LA CIUDAD DE MÉXICO Y DE LA LEY DE ACCESO DE LAS MUJERES A UNA VIDA LIBRE DE VIOLENCIA DE LA CIUDAD DE MÉXICO', 'CON PROYECTO DE DECRETO, POR EL QUE SE REFORMAN, MODIFICAN Y ADICIONAN DIVERSAS DISPOSICIONES DE LEY ORGANICA DEL CONGRESO DE LA CIUDAD DE MÉXICO Y DE LA LEY DE ACCESO DE LAS MUJERES A UNA VIDA LIBRE DE VIOLENCIA DE LA CIUDAD DE MÉXICO', 21, 2, 'DESCARGAR ARCHIVO ADJUNTO', 'DESCARGAR ARCHIVO ADJUNTO', '22', '25', NULL, 'files/iniciativa/indogl2.pdf'),
(420, '2019-08-22', '2019-09-05', 'REFORMA', 'LEY ORGÁNICA DEL PODER EJECUTIVO Y DE LA ADMINISTRACIÓN PÚBLICA DE LA CIUDAD DE MÉXICO', 'CON PROYECTO DE DECRETO, POR EL QUE SE REFORMA LA FRACCIÓN XVI DEL ARTÍCULO 33 DE LA LEY ORGÁNICA DEL PODER EJECUTIVO Y DE LA ADMINISTRACIÓN PÚBLICA DE LA CIUDAD DE MÉXICO; ', 65, 8, 'DESCARGAR ARCHIVO ADJUNTO', 'DESCARGAR ARCHIVO ADJUNTO', '3', '25', NULL, 'files/iniciativa/inievp3.pdf'),
